import styled from 'styled-components';

export const HomeWrapper = styled.div`
  width: 960px;
  margin: 0 auto;
  height: 300px;
`

export const HomeLeft = styled.div`
  float: left;
  margin-left: 15px;
  padding-top: 30px;
  width: 625px;
  .banner-img{
    width:625px;
    height:270px;
  }
`

export const HomeRight = styled.div`
  float: right;
  width: 240px;
`

export const TopicWrapper = styled.div`
  padding: 20px 0 10px 0;
  
`
