import { fromJS } from 'immutable';

const defaultState = fromJS({
  topicList: [
    {
      id: 1,
      title: '社会热点',
      imgUrl: 'https://upload-images.jianshu.io/upload_images/24196296-0de67a7f2d03cf95?imageMogr2/auto-orient/strip|imageView2/1/w/360/h/240'
    },
    {
      id: 2,
      title: '彩绘',
      imgUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSyPRNsg_LeKvcfgpr3V3YrxG971I6g9OXJig&amp;usqp=CAU'
    }
  ],

  article: [
    {
      id: 1,
      title: '投资者对《2077》不满 律师事务所对CDPR提起集体诉讼',
      desc: '之前CDPR宣布《赛博朋克2077》全球销量已超过1300万份，使其成为有史以来最成功的RPG游戏之一。但现在一盆冷水泼在CDPR头上了，全球投资者权益律师事务所罗森律师事务所(Rosen Law Firm)宣布，已代表2020年1月16日至2020年12月17日期间CDPR的证券购买者提起了集体诉讼...',
      imgUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTGDCUNlgPpkxYfau1l_OqULlB58tmw5M9kaQ&usqp=CAU'
    },
    {

    }
  ]
});

export default (state = defaultState, action) => {
  switch(action.type){
    default:
      return state;
  }
}
