import React ,{ Component } from 'react';
import { connect } from 'react-redux';
import { 
  TopicWrapper,
  TopicItem
 } from '../style';

class Topic extends Component{

  render(){
    return(
      <TopicWrapper>
        {
          this.props.list.map((item) => {
            return (
              <TopicItem key={item.get('id')}>
                <img 
                  className = 'topic-pic' 
                  src={item.get('imgUrl')}
                />
                {item.get('title')}
              </TopicItem>
            )
          })
        }
        <TopicItem>
          <img 
            className = 'topic-pic' 
            src="https://upload-images.jianshu.io/upload_images/24196296-0de67a7f2d03cf95?imageMogr2/auto-orient/strip|imageView2/1/w/360/h/240" alt="120"
          />
          社会热点
        </TopicItem>
      </TopicWrapper>
    )
  }

}

const mapState = (state) => ({
  list: state.get('home').get('topicList')
});

export default connect(mapState, null)(Topic);
