import { fromJS } from 'immutable';

const defaultState = fromJS({
  topicList: [
    {
      id: 1,
      title: '社会热点',
      imgUrl: 'https://upload-images.jianshu.io/upload_images/24196296-0de67a7f2d03cf95?imageMogr2/auto-orient/strip|imageView2/1/w/360/h/240'
    },

    {
      id: 2,
      title: '彩绘',
      imgUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSyPRNsg_LeKvcfgpr3V3YrxG971I6g9OXJig&amp;usqp=CAU'
    }
  ]
});

export default (state = defaultState, action) => {
  switch(action.type){
    default:
      return state;
  }
}
